function doGet(e) {

  try {
  Logger.log(JSON.stringify(e));

var ss = SpreadsheetApp.openById('1nsQyMS-bwCz32FwX8FD3E9MtCG1vFtUfyNxoqRPuS10');
var sheet = ss.getSheets()[0];
var ratesData = sheet.getRange(2,1,sheet.getLastRow()-1,8).getValues()

    // return json success results
  return ContentService.createTextOutput(JSON.stringify(ratesData))
    .setMimeType(ContentService.MimeType.JSON);


  } catch(error) { // if error return this
    Logger.log(error);
    return ContentService
          .createTextOutput(JSON.stringify({result:'error'}))
          .setMimeType(ContentService.MimeType.JAVASCRIPT);
  }
}
