function doPost(e) {
  Logger.log(JSON.stringify(e));
  var email = e.parameter['email'];
  // var firstName = e.parameter['firstName'];
  // var lastName = e.parameter['lastName'];

  try {

    var emailBody = HtmlService.createTemplateFromFile('purchaseConfirmation.html');
    // emailBody.firstName = firstName;
    // emailBody.lastName = lastName;
    var message = emailBody.evaluate().getContent()

    MailApp.sendEmail({
      to: email.toString(),
      subject: "Confirmación de Pago | XXXVII Jornadas Científicas",
      htmlBody: message
    });


    // return json success results
    return ContentService.createTextOutput(JSON.stringify({ result: 'success' }))
      .setMimeType(ContentService.MimeType.JAVASCRIPT);

  } catch (error) { // if error return this
    Logger.log(error);
    return ContentService
      .createTextOutput(JSON.stringify({ result: 'error' }))
      .setMimeType(ContentService.MimeType.JAVASCRIPT);
  }
}
